import React, { useState } from 'react';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';

const MyTeams = () => {
  const [team, setTeam] = useState('');
  const [league, setLeague] = useState('');
  const [favorites, setFavorites] = useState(['Dbacks', 'Suns', 'Coyotes']);
  return (
    <div>
      <h2>My Favorites</h2>
      <Grid container>
        <Grid item xs={12} md={5}>
          <input id="pick-team" type="text" value={team} onChange={value => setTeam(value)} placeholder="Pick your teams" />
          <h3>Recommended</h3>
          <ul>
            <li><Button onClick={() => setFavorites(prevFaves => [...prevFaves, 'Dodgers'])}>Dodgers <img src={require('../images/dbacks.png')} alt="Arizona Diamondbacks" height="25px" /></Button></li>
            <li><Button onClick={() => setFavorites(prevFaves => [...prevFaves, 'Lakers'])}>Lakers <img src={require('../images/suns.png')} alt="Phoenix Suns" height="25px" /></Button></li>
            <li><Button onClick={() => setFavorites(prevFaves => [...prevFaves, 'Sharks'])}>Sharks <img src={require('../images/coyotes.png')} alt="Phoenix Coyotes" height="25px" /></Button></li>
          </ul>
        </Grid>
        <Grid item xs={12} md={2}>
          Or
        </Grid>
        <Grid item xs={12} md={5}>
          <input id="pick-league" type="text" value={league} onChange={value => setLeague(value)} placeholder="Pick your leagues" />
          <h3>Recommended</h3>
          <ul>
            <li><Button onClick={() => setFavorites(prevFaves => [...prevFaves, 'National League West'])}>National League West (MLB) <img src={require('../images/dbacks.png')} alt="Arizona Diamondbacks" height="25px" /></Button></li>
            <li><Button onClick={() => setFavorites(prevFaves => [...prevFaves, 'Pacific Division'])}>Pacific Division (NHL) <img src={require('../images/suns.png')} alt="Phoenix Suns" height="25px" /></Button></li>
            <li><Button onClick={() => setFavorites(prevFaves => [...prevFaves, 'NFC West'])}>NFC West (NFL) <img src={require('../images/cardinals.png')} alt="Arizona Cardinals" height="25px" /></Button></li>
          </ul>
        </Grid>
      </Grid>
      <h3>My Favorites</h3>
      <ul>
        {favorites.map((favorite) => (
          <li key={favorite}>
            <Button onClick={() => setFavorites(prevFaves => prevFaves.filter(fave => fave !== favorite))}>
              {favorite}
            </Button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MyTeams;
