import React, { Fragment, useEffect, useState } from 'react';
import Grid from '@material-ui/core/Grid';
import MUIDataTable from 'mui-datatables';
import './App.scss';

const App = () => {
  const [isCompact, setIsCompact] = useState(window.innerWidth < 960);
  useEffect(() => {
    window.addEventListener('resize', () => setIsCompact(window.innerWidth < 960));
    return () => window.removeEventListener('resize', setIsCompact);
  });

  const createData = (date, time, game, tv, premium, free, highlights) => ({
    date, time, game, tv, premium, free, highlights,
  });

  const columns = [
    { name: 'date', label: 'Date' },
    { name: 'time', label: 'Time' },
    { name: 'game', label: 'Game' },
    { name: 'tv', label: 'TV' },
    { name: 'premium', label: 'Premium Streams' },
    { name: 'free', label: 'Free Streams' },
    { name: 'highlights', label: 'Highlights' },
  ];

  const convertToIcons = (text) => {
    const splitText = text.split('@');
    return (
      <div>
        <img src={require(`../images/${splitText[0].trim().toLowerCase()}.png`)} height="25px" alt={splitText[0]} />
        <span style={{ verticalAlign: 'middle' }}>@</span>
        <img src={require(`../images/${splitText[1].trim().toLowerCase()}.png`)} height="25px" alt={splitText[1]} />
      </div>
    );
  };

  const rows = [
    createData('Sept 18', '9:55am', isCompact ? convertToIcons('Tottenham @ Chelsea') : 'Tottenham @ Chelsea', 'TNT', <a href="/">TNT</a>, <a href="/">Reddit</a>, ''),
    createData('Sept 19', '9:55am', isCompact ? <img src={require('../images/diamondbacks.png')} height="25px" alt="Dbacks" /> : 'Dbacks @ Padres', 'FOX Sports AZ', <a href="/">ABC</a>, <a href="/">Reddit</a>, <a href="/">ESPN</a>),
    createData('Sept 20', '9:55am', isCompact ? <img src={require('../images/suns.png')} height="25px" alt="Suns" /> : 'Suns @ Kings', 'FOX Sports AZ', <a href="/">CBS</a>, <a href="/">Reddit</a>, ''),
    createData('Sept 21', '9:55am', isCompact ? <img src={require('../images/coyotes.png')} height="25px" alt="Coyotes" /> : 'Coyotes @ Sharks', 'FOX Sports AZ', <a href="/">FOX</a>, <a href="/">Reddit</a>, ''),
    createData('Sept 22', '9:55am', isCompact ? <img src={require('../images/cardinals.png')} height="25px" alt="Cardinals" /> : 'Cardinals @ Panthers', 'FOX', <a href="/">NBC</a>, <a href="/">Reddit</a>, ''),
    createData('Sept 23', '9:55am', isCompact ? <img src={require('../images/masters.png')} height="25px" alt="Masters" /> : 'Masters', 'NBC', <a href="/">ESPN2</a>, <a href="/">Reddit</a>, ''),
  ];


  return (
    <div>
      <Grid container>
        <Grid container>
          <header style={{ width: '100%', background: 'black', color: 'white' }}>
            <h1 id="header-title" style={{ fontSize: isCompact ? '36px' : '56px', textAlign: 'center', textShadow: '2px 2px orange' }}>SPORTSBOT5000</h1>
            <nav id="header-links" style={{ textAlign: 'center' }}>
              <a href="/" style={{ display: 'inline-block', padding: '10px', color: 'white' }}>My Games</a>
              <a href="/" style={{ display: 'inline-block', padding: '10px', color: 'white' }}>Popular Games</a>
              <a href="/" style={{ display: 'inline-block', padding: '10px', color: 'white' }}>My Teams</a>
              <a href="/" style={{ display: 'inline-block', padding: '10px', color: 'white' }}>Account</a>
