import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import './App.scss';

const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing(3),
    overflowX: 'auto',
  },
  table: {
    minWidth: 350,
  },
}));

const createData = (date, time, game, tv, premium, free, highlights) => ({
  date, time, game, tv, premium, free, highlights,
});

const tableHeaders = [
  'Date',
  'Time',
  'Game',
  'TV',
  'Premium Streams',
  'Free Streams',
  'Highlights',
];

const rows = [
  createData('Sept 18', '9:55am', 'Tottenham@Chelsea', 'TNT', <a href="/">TNT</a>, <a href="/">Reddit</a>, ''),
  createData('Sept 19', '9:55am', 'Dbacks@Padres', 'FOX Sports AZ', <a href="/">ABC</a>, <a href="/">Reddit</a>, <a href="/">ESPN</a>),
  createData('Sept 20', '9:55am', 'Suns@Kings', 'FOX Sports AZ', <a href="/">CBS</a>, <a href="/">Reddit</a>, ''),
  createData('Sept 21', '9:55am', 'Coyotes@Sharks', 'FOX Sports AZ', <a href="/">FOX</a>, <a href="/">Reddit</a>, ''),
  createData('Sept 22', '9:55am', 'Cardinals@Panthers', 'FOX', <a href="/">NBC</a>, <a href="/">Reddit</a>, ''),
  createData('Sept 23', '9:55am', 'Wimbledon Finals - Men\'s Singles', 'ESPN2', <a href="/">ESPN2</a>, <a href="/">Reddit</a>, ''),
];

const App = () => {
  const { table, root } = useStyles();
  const [checked, setChecked] = useState(['Date', 'Time', 'Game', 'TV', 'Premium Streams', 'Free Streams', 'Highlights']);

  function handleToggle(value) {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  return (
    <div className={`App ${root}`}>

      <List disablePadding className={root}>
        {tableHeaders.slice(3).map(value => {
          const labelId = `checkbox-list-label-${value.length === 1 ? value : value.split(' ').join(',')}`;

          return (
            <ListItem
              key={value}
              role={undefined}
              dense
              button
              onClick={() => handleToggle(value)}
              padding="none"
            >
              <Checkbox
                edge="start"
                checked={checked.indexOf(value) !== -1}
                tabIndex={-1}
                disableRipple
                inputProps={{ 'aria-labelledby': labelId }}
              />
              <ListItemText id={labelId} primary={value} />
            </ListItem>
          );
        })}
      </List>

      <Table className={table}>
        <TableHead>
          <TableRow>
            {checked.map(header => (
              <TableCell align="center" key={`header-${header}`}>{header}</TableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map(({
            date, time, game, tv, premium, free, highlights,
          }) => (
              <TableRow key={`${date}-${time}-${game}`}>
                {checked.indexOf('Date') !== -1 && (
                  <TableCell component="th" scope="row" align="center">{date}</TableCell>
                )}
                {checked.indexOf('Time') !== -1 && (
                  <TableCell align="center">{time}</TableCell>
                )}
                {checked.indexOf('Game') !== -1 && (
                  <TableCell align="center">{game}</TableCell>
                )}
                {checked.indexOf('TV') !== -1 && (
                  <TableCell align="center">{tv}</TableCell>
                )}
                {checked.indexOf('Premium Streams') !== -1 && (
                  <TableCell align="center">{premium}</TableCell>
                )}
                {checked.indexOf('Free Streams') !== -1 && (
                  <TableCell align="center">{free}</TableCell>
                )}
                {checked.indexOf('Highlights') !== -1 && (
                  <TableCell align="center">{highlights}</TableCell>
                )}
              </TableRow>
            ))}
        </TableBody>
      </Table>
    </div>
  );
};

export default App;
