import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
// import Grid from '@material-ui/core/Grid';
// import Table from '@material-ui/core/Table';
// import TableBody from '@material-ui/core/TableBody';
// import TableCell from '@material-ui/core/TableCell';
// import TableHead from '@material-ui/core/TableHead';
// import TableRow from '@material-ui/core/TableRow';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import MUIDataTable from 'mui-datatables';
import './App.scss';

const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    // marginTop: theme.spacing(3),
    overflowX: 'auto',
  },
  table: {
    minWidth: 350,
  },
  button: {
    margin: theme.spacing(1),
    display: 'block',
    textTransform: 'capitalize',
  },
  rightIcon: {
    marginLeft: theme.spacing(1),
    verticalAlign: 'bottom',
  },
}));

const createData = (date, time, game, tv, premium, free, highlights) => ({
  date, time, game, tv, premium, free, highlights,
});

const columns = [
  { name: 'date', label: 'Date', options: { sort: true } },
  { name: 'time', label: 'Time', options: { sort: true } },
  { name: 'game', label: 'Game', options: { sort: true } },
  { name: 'tv', label: 'TV', options: { sort: true } },
  { name: 'premium', label: 'Premium Streams', options: { sort: true } },
  { name: 'free', label: 'Free Streams', options: { sort: true } },
  { name: 'highlights', label: 'Highlights', options: { sort: true } },
];

const rows = [
  createData({ date: 'Sept 18', time: '9:55am', game: 'Tottenham@Chelsea', tv: 'TNT', premium: <a href="/">TNT</a>, free: <a href="/">Reddit</a>, highlights: '' }),
  createData({ date: 'Sept 19', time: '9:55am', game: 'Dbacks@Padres', tv: 'FOX Sports AZ', premium: <a href="/">ABC</a>, free: <a href="/">Reddit</a>, highlights: <a href="/">ESPN</a> }),
  createData({ date: 'Sept 20', time: '9:55am', game: 'Suns@Kings', tv: 'FOX Sports AZ', premium: <a href="/">CBS</a>, free: <a href="/">Reddit</a>, highlights: '' }),
  createData({ date: 'Sept 21', time: '9:55am', game: 'Coyotes@Sharks', tv: 'FOX Sports AZ', premium: <a href="/">FOX</a>, free: <a href="/">Reddit</a>, highlights: '' }),
  createData({ date: 'Sept 22', time: '9:55am', game: 'Cardinals@Panthers', tv: 'FOX', premium: <a href="/">NBC</a>, free: <a href="/">Reddit</a>, highlights: '' }),
  createData({ date: 'Sept 23', time: '9:55am', game: 'Wimbledon Finals - Men\'s Singles', tv: 'ESPN2', premium: <a href="/">ESPN2</a>, free: <a href="/">Reddit</a>, highlights: '' }),
];

const App = () => {
  const { button, rightIcon, root, table } = useStyles();
  const [checked, setChecked] = useState(['Date', 'Time', 'Game', 'TV', 'Premium Streams', 'Free Streams', 'Highlights']);
  const [showFilters, setShowFilters] = useState(false);

  function handleToggleCheckbox(value) {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  return (
    <div className={`App ${root}`}>
      <Button className={button} variant="contained" onClick={() => setShowFilters(!showFilters)}>
        Filters
        {showFilters ? <ExpandLess className={rightIcon} /> : <ExpandMore className={rightIcon} />}
      </Button>
      {showFilters && (
        <List disablePadding className={root}>
          {columns.slice(3).map(value => {
            const labelId = `checkbox-list-label-${value.length === 1 ? value : value.split(' ').join(',')}`;

            return (
              <ListItem
                key={value}
                role={undefined}
                dense
                button
                onClick={() => handleToggleCheckbox(value)}
                padding="none"
              >
                <Checkbox
                  edge="start"
                  checked={checked.indexOf(value) !== -1}
                  tabIndex={-1}
                  disableRipple
                  inputProps={{ 'aria-labelledby': labelId }}
                />
                <ListItemText id={labelId} primary={value} />
              </ListItem>
            );
          })}
        </List>
      )}

      <MUIDataTable
        title={"My Matches"}
        data={rows}
        columns={columns}
        options={{ responsive: 'stacked' }}
      />

      {/*
      <Grid container spacing={1}>
        <Grid item xs zeroMinWidth>
          <Table className={table}>
            <TableHead>
              <TableRow>
                <Grid container>
                  {checked.map(header => (
                    <Grid item xs zeroMinWidth direction="row" border={1}>
                      <TableCell align="center" key={`header-${header}`}>{header}</TableCell>
                    </Grid>
                  ))}
                </Grid>
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map(({
                date, time, game, tv, premium, free, highlights,
              }) => (
                  <TableRow key={`${date}-${time}-${game}`}>
                    <Grid container>
                      {checked.indexOf('Date') !== -1 && (
                        <Grid item xs zeroMinWidth direction="row">
                          <TableCell component="th" scope="row" align="center">{date}</TableCell>
                        </Grid>
                      )}
                      {checked.indexOf('Time') !== -1 && (
                        <Grid item xs zeroMinWidth direction="row">
                          <TableCell align="center">{time}</TableCell>
                        </Grid>
                      )}
                      {checked.indexOf('Game') !== -1 && (
                        <Grid item xs={2} zeroMinWidth direction="row">
                          <TableCell align="center">{game}</TableCell>
                        </Grid>
                      )}
                      {checked.indexOf('TV') !== -1 && (
                        <Grid item xs zeroMinWidth direction="row">
                          <TableCell align="center">{tv}</TableCell>
                        </Grid>
                      )}
                      {checked.indexOf('Premium Streams') !== -1 && (
                        <Grid item xs zeroMinWidth direction="row">
                          <TableCell align="center">{premium}</TableCell>
                        </Grid>
                      )}
                      {checked.indexOf('Free Streams') !== -1 && (
                        <Grid item xs zeroMinWidth direction="row">
                          <TableCell align="center">{free}</TableCell>
                        </Grid>
                      )}
                      {checked.indexOf('Highlights') !== -1 && (
                        <Grid item xs zeroMinWidth direction="row">
                          <TableCell align="center">{highlights}</TableCell>
                        </Grid>
                      )}
                    </Grid>
                  </TableRow>
                ))}
            </TableBody>
          </Table>
        </Grid>
      </Grid>
      */}
    </div>
  );
};

export default App;
